@section('javascript')
    <script>
        $("#nombres,#apellidos,#cargo,#departamento,#ciudad,#direccion,#telefono,#extension").on("keyup", function() {
            $("#nombreTarjeta").text($("#nombres").val() + ' ' + $("#apellidos").val());
            $("#cargoTarjeta").text($("#cargo").val() + '-' + $("#departamento").val());
            $("#direccionTarjeta").text($("#ciudad").val() + '-' + $("#direccion").val());
            $("#TelefonoTarjeta").text($("#telefono").val() + ' (' + $("#extension").val() + ')');
        });

        function data() {
            $("#cargaImagen").empty();
            html2canvas($("#contenido"), {
                onrendered: function(canvas) {
                    theCanvas = canvas;
                    var dataAnterior = document.getElementById("cargaImagen");

                    document.getElementById("cargaImagen").appendChild(canvas);
                    $("#datosImagen").text(
                        'Puedes hacer click con el bot√≥n derecho del Mouse para Guardar la imagen.');

                }
            });
        }
    </script>
    <script src="{{ url('adminlte3/plugins/canvas/filesaver.js') }}" type="text/javascript"></script>
    <script src="{{ url('adminlte3/plugins/canvas/html2canvas.js') }}" type="text/javascript"></script>

    <script src="{{ url('adminlte3/plugins/datepicker/bootstrap-datepicker.js') }}"></script>
